function Timer(e,t){this.speed=e;this.interval=t}function Loader(e){Timer.call(this,e.speed,e.interval);this.id=e.id;this.color=e.color;this.theme=e.theme;this.dimension=e.dimension;this.startAngle=4.71238898038469;this.init(this.id);this.count(0)}function drawCircle(e){return'<div id="'+e+'_screen"><h3>0%</h3></div>'+'<canvas id="'+e+'_keyframes"/>'}function round5(e){return e%5>=2.5?parseInt(e/5)*5+5:parseInt(e/5)*5}Timer.prototype.count=function(e){var t=this;setTimeout(function(){t.render(e);e++;if(e<t.interval){t.count(e)}},t.speed)};Timer.prototype.render=function(){};Loader.prototype=Object.create(Timer.prototype);Loader.prototype.init=function(){this.container=document.getElementById(this.id);this.container.innerHTML=drawCircle(this.id);this.screen=document.getElementById(this.id+"_screen");this.canvas=document.getElementById(this.id+"_keyframes");this.ctx=this.canvas.getContext("2d");this.config(this.theme,this.dimension,this.color,this.speed)};Loader.prototype.render=function(e){this.screen.innerHTML="<h3>"+round5(100/this.interval*(e+1))+"%</h3>";this.ctx.beginPath();this.ctx.moveTo(this.canvas.width/2,this.canvas.height/2);this.ctx.arc(this.canvas.width/2,this.canvas.height/2,this.canvas.height/2,this.startAngle,this.startAngle+.01+Math.PI*2*(360/this.interval/360),false);this.ctx.fill();this.startAngle+=Math.PI*2*(360/this.interval/360)};Loader.prototype.config=function(e,t,n,r){var i=0;switch(t){case"small":i=100;break;case"medium":i=150;break;case"large":i=200;break;default:i=100;t="small"}this.canvas.width=i;this.canvas.height=i;this.container.className+="loader "+e+" "+t;this.screen.className+="screen "+e+" "+t+"_screen";if(n==null)this.ctx.fillStyle="black";else this.ctx.fillStyle=n;if(isNaN(r))this.speed=1e3};if(typeof Object.create!=="function"){Object.create=function(e){var t=function(){};t.prototype=e;return new t}}